<div class="container">
  <div class="checkboxes-section">
    <button class="prev">&lt;</button>
    <div class="checkboxes">
      <div class="checkbox-group">
        <div class="checkbox-item">
          <input id="allfoodtypes" data-search-term="all" type="checkbox">
          <label class="inline-checkbox" for="allfoodtypes">All recipes</label>
        </div>
        <div class="checkbox-item">
          <input id="vegetable" data-search-term="vegetable" type="checkbox">
          <label class="inline-checkbox" for="vegetable">Vegetable</label>
        </div>
        <div class="checkbox-item">
          <input id="seafood" data-search-term="seafood" type="checkbox">
          <label class="inline-checkbox" for="seafood">Sea Foods</label>
        </div>
        <div class="checkbox-item">
          <input id="breakfast" data-search-term="breakfast" type="checkbox">
          <label class="inline-checkbox" for="breakfast">Breakfast</label>
        </div>
        <div class="checkbox-item">
          <input id="drinks" data-search-term="drinks" type="checkbox">
          <label class="inline-checkbox" for="drinks">Drinks</label>
        </div>
        <div class="checkbox-item">
          <input id="lunch" data-search-term="lunch" type="checkbox">
          <label class="inline-checkbox" for="lunch">Lunch</label>
        </div>
        <div class="checkbox-item">
          <input id="snacks" data-search-term="snacks" type="checkbox">
          <label class="inline-checkbox" for="snacks">Snacks</label>
        </div>
        <div class="checkbox-item">
          <input id="dinner" data-search-term="dinner" type="checkbox">
          <label class="inline-checkbox" for="dinner">Dinner</label>
        </div>
        <div class="checkbox-item">
          <input id="poultry" data-search-term="poultry" type="checkbox">
          <label class="inline-checkbox" for="poultry">Poultry</label>
        </div>
        <div class="checkbox-item">
          <input id="other" data-search-term="other" type="checkbox">
          <label class="inline-checkbox" for="other">Other</label>
        </div>
      </div>
    </div>
    <button class="next">&gt;</button>
  </div>

  <div class="fast-filter">
    <div id="dropdown" class="dropdown">
      <div id="btn" class="btn">
        <input id="filter" placeholder="What are we eating today, Pénélope?" type="text" class="eatburnsleep">
        <img id="loupecopie" class="loupecopie" src="data:image/png;base64,">
      </div>
    </div>
  </div>
